package week1.day1;

public class ControlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=0;i<=20;i++)
		if 
			(i%2==1) {
			System.out.println("The given number"+" "+i+" "+"is odd");
		}else {
			System.out.println("The given number"+" "+i+" "+"is even");
		}

	}

}
